import React from 'react';
import Slider from 'react-slick';
import { useSwipeable } from 'react-swipeable';

import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

let slider; // Referência ao slider

const Carousel = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  const handlers = useSwipeable({
    onSwipedLeft: () => {
      slider.slickNext();
    },
    onSwipedRight: () => {
      slider.slickPrev();
    },
  });

  return (
    <div {...handlers}>
      <Slider ref={(c) => (slider = c)} {...settings}>
        <div className="carousel-item">
          <img src="caminho-para-sua-imagem-1.jpg" alt="Imagem 1" />
        </div>
        <div className="carousel-item">
          <img src="caminho-para-sua-imagem-2.jpg" alt="Imagem 2" />
        </div>
        <div className="carousel-item">
          <img src="caminho-para-sua-imagem-3.jpg" alt="Imagem 3" />
        </div>
        {/* Adicione mais divs para mais imagens */}
      </Slider>
    </div>
  );
};

export default Carousel;
